    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Metodo de la burbuja
        miEmpresa1.OrdenBurbuja();
        String mensaje = "";
        for (int i = 0; i < miEmpresa1.getNumeroDeEmpleados(); i++) {
            mensaje = mensaje
                    + miEmpresa1.getCodigoDelEmpleado(i) + "---"
                    + miEmpresa1.getNombreDelEmpleado(i) + "---"
                    + miEmpresa1.getSueldoDelEmpleado(i) + "---" + "\n";
        }
        System.out.println(mensaje);
        txtComparaciones.setText(String.valueOf(miEmpresa1.getComparaciones()));
        txtIntercambios.setText(String.valueOf(miEmpresa1.getIntercambios()));

    }//GEN-LAST:event_jButton1ActionPerformed

