    private void btnSecOrdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSecOrdActionPerformed
        Integer pos = miEmpresa3.BusquedaSecuencialOrdenado(Integer.parseInt(txtCod.getText()));
        if (pos == -1) {
            txtPos.setText("no encontrado");
        } else {
            txtPos.setText(miEmpresa3.getEmpleados()[pos].toString());
        }
        String mensaje = "";
        for (int i = 0; i < miEmpresa3.getNumeroDeEmpleados(); i++) {
            mensaje = mensaje + miEmpresa3.getCodigoDelEmpleado(i) + "---"
                    + miEmpresa3.getNombreDelEmpleado(i) + "---"
                    + miEmpresa3.getSueldoDelEmpleado(i) + "\n";
        }
        System.out.println(mensaje);
        txtComparaciones.setText(String.valueOf(miEmpresa3.getComparaciones()));
    }//GEN-LAST:event_btnSecOrdActionPerformed

